mapper.update:
UPDATE manage_user_account
SET email = #{email}, mobilephone = #{mobilephone}, role_id = #{role.id}
WHERE id = #{id}

UPDATE manage_user_account
SET password = #{password}, salt = #{salt}
WHERE id = #{id}

UPDATE manage_user_account
SET status = (CASE status WHEN 0 THEN 1 ELSE 0 END)
WHERE id = #{id}
